import Chat from "./Chat";
import Login from "./Login";
import ProfileBubble from "./ProfileBubble";
import {useSelector} from "react-redux";
import { LOGIN_STATE } from "../redux/stateConstants";

const Sidebar = () => {
    // TODO: get users online
    // or whatever
    // probably store in store; when client joins:

    // TODO: either cap amount of users or scroll?
    // overflow-y...
    const users = Array.from(Array(10), (num, index) => index + 1);
    const loginState = useSelector(state => state.loginState);
    // console.log(users)

    return (
        <div className="container sidebar-profile-container container-no-margins">
            {loginState === LOGIN_STATE.LOGGED_OUT && <Login />}
            {loginState === LOGIN_STATE.LOGGED_IN && 
            <aside className="profile-bubble-column pe-5">
                {users.map((number, index) => (
                    <ProfileBubble key={index} username={`user${number}`} />
                ))}
            </aside>
             && <Chat/>}
            {loginState === LOGIN_STATE.INVALID_LOGIN && <h1>login invalid</h1>}
        </div>
    )
}

export default Sidebar
