import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart, faLaughSquint, faSadCry, faAngry } from "@fortawesome/free-regular-svg-icons";
import { faHeart as solidHeart, faLaughSquint as solidSquint, faSadCry as solidCry, faAngry as solidAngry } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from 'react';
import {  addReacts} from "../redux/actions";
import { useSelector,useDispatch } from 'react-redux';

const Reacts = (props) => {


    const [heartFilled, toggleHeartFilled] = useState(false)
    const [laughFilled, toggleLaughFilled] = useState(false)
    const [cryFilled, toggleCryFilled] = useState(false)
    const [angryFilled, toggleAngryFilled] = useState(false)
    const posts = useSelector(state => state.posts)

    const dispatch = useDispatch();

    //const [reacts, updateReacts] = useState(props.post.reacts.heart + props.post.reacts.laugh + props.post.reacts.cry + props.post.reacts.angry)

    const reacted = (reaction) => {
        console.log("reaction: ", reaction)
        let number = props.post.reacts.heart
        console.log("number: ", number)
        if (reaction === "heart") {
            toggleHeartFilled(!heartFilled)
            console.log("heartFilled: ", number)
            number = (heartFilled) ?
                number + 1: number - 1;
            dispatch(addReacts({...props.post.reacts,
                heart: number
            }, props.id))
        }
    }

    useEffect(() => {
        //console.log("reacts: ", reacts)
        //updateReacts(reacts + 1)

        /* if (heartFilled) {
            dispatch(addReacts({...props.post.reacts,
                heart: props.post.reacts.heart + 1
            }, props.id))
        } else  */if (laughFilled) {
            dispatch(addReacts({...props.post.reacts,
                laugh: props.post.reacts.laugh + 1
            },props.id))
        } else if (cryFilled) {
            dispatch(addReacts({...props.post.reacts,
                cry: props.post.reacts.cry + 1
            },props.id))
        } else if (angryFilled) {
            dispatch(addReacts({...props.post.reacts,
                angry: props.post.reacts.angry + 1
            },props.id))
        }

    },[laughFilled, cryFilled, angryFilled])

    return(
        <div className="reacts">
            {
                heartFilled ? <FontAwesomeIcon className="reacts heart-color" icon={solidHeart} size="2x" onClick={(e) => toggleHeartFilled(!heartFilled)}/>:
                <FontAwesomeIcon className="reacts" icon={faHeart} value="heart" size="2x" onClick={(e) => reacted(e.target.dataset.icon)}/>
            }
            {
                laughFilled ? <FontAwesomeIcon className="reacts laugh-color" icon={solidSquint} size="2x" onClick={(e) => toggleLaughFilled(!laughFilled)}/>:
                <FontAwesomeIcon className="reacts" icon={faLaughSquint} size="2x" onClick={(e) => toggleLaughFilled(!laughFilled)}/>
            }
            {
                angryFilled ? <FontAwesomeIcon className="reacts angry-color" icon={solidAngry} size="2x" onClick={(e) => toggleAngryFilled(!angryFilled)}/>:
                <FontAwesomeIcon className="reacts" icon={faAngry}size="2x" onClick={(e) => toggleAngryFilled(!angryFilled)}/>
            }
            {
                cryFilled ? <FontAwesomeIcon className="reacts sad-color" icon={solidCry} size="2x" onClick={(e) => toggleCryFilled(!cryFilled)}/>:
                <FontAwesomeIcon className="reacts" icon={faSadCry} size="2x" onClick={(e) => toggleCryFilled(!cryFilled)}/>
            }
            <p class="card-text">{props.post.reacts.heart + props.post.reacts.cry + props.post.reacts.laugh + props.post.reacts.angry + " reacts"}</p>

        </div>
    )
}

export default Reacts
