import Reacts from "./Reacts"
import { useSelector } from "react-redux"
import Reply from "./Reply"
//use id to pull post from data store, useSelector

const Post = ({id}) => {
    const posts = useSelector(state => state.posts);


    return (
        <div class="card mb-3 post">
            <img src={posts[id].pictureUrl} class="card-img-top post-img" alt="..."/>
            <div class="card-body">
                <Reacts />
                <h5 class="card-title">{posts[id].username}</h5>
                <p class="card-text">{posts[id].caption}</p>
                <div className="row">
                {
                    posts[id].tags.map((tag, i) => 
                        <div className="col">


                        </div>

                    )
                }
                </div>
                {
                    posts[id].replies.map((reply,i) => 
                        <div className="row">
                            <p className="col-3 card-text username-reply">{reply.user}</p>
                            <p className="col-9 card-text">{reply.reply}</p>
                        </div>
                    )
                }
                <Reply id={id}/>
                <p class="card-text"><small class="text-muted">{posts[id].datePosted}</small></p>

            </div>
        </div>
    )

}

export default Post
