import { COMPLETE_ONBOARDING, STORE_POSTS, LOGIN_SUCCESS, INVALID_LOGIN, 
  LOGIN_NETWORK_ERROR, LOGOUT, NEW_MESSAGE, CONNECTED, STORE_USERS } from "./actionConstants";
import firebase from "../data/fbConfig";
import store from "./store";
import { joinChat, sendMessage } from "../client";

const database = firebase.firestore();

export const loginSuccess = user => ({
    type: LOGIN_SUCCESS,
    payload: {
        user: user
    }
});

export const loginFail = () => ({
    type: INVALID_LOGIN
});

export const loginNetworkError = () => ({
    type: LOGIN_NETWORK_ERROR
});

export const logout = () => ({
    type: LOGOUT
});

export const userCompletedOnboarding = userId => {

    return true; // A real operation would have a return...
}

export const completeOnboarding = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    userCompletedOnboarding(userId);
    return {
        type: COMPLETE_ONBOARDING
    }
}

export const storePosts = (posts) => ({
    type: STORE_POSTS,
    payload: {
        posts: posts
    }

})

export const getPosts = () => {
    return dispatch => {

      database.collection("posts")
        .get()
        .then((querySnapshot) => {
          if (querySnapshot.size > 0) {
            const posts = {};
            querySnapshot.forEach(doc => {
              posts[doc.id] = {
                caption:doc.data().caption,
                datePosted:doc.data().datePosted,
                pictureUrl:doc.data().pictureUrl,
                reacts:doc.data().reacts,
                replies:doc.data().replies,
                tags:doc.data().tags,
                userId:doc.data().userId,
                username:doc.data().username
              }
            })
            dispatch(storePosts(posts));
          }
          else {
            console.log("No posts available") 
          }
        })
        .catch(error => {
          console.log("Error")
        });
    }
  };

export const storeUsers = (users) => ({
  type: STORE_USERS,
  payload: {
      users: users
  }
})

export const getUsers = () => {
  return dispatch => {
    const database = firebase.firestore();
    database.collection("users")
      .get()
      .then((querySnapshot) => {
        if (querySnapshot.size > 0) {
          const userDatabase = {};
          querySnapshot.forEach(doc => {
            userDatabase[doc.id] = {
              username:doc.data().username,
              password:doc.data().password,
              isNewUser:doc.data().isNewUser,
              isPublic:doc.data().isPublic,
              profilePicture:doc.data().profilePicture,
              friendsList:doc.data().friendsList
            }
          })
          dispatch(storeUsers(userDatabase));
        }
        else {
          console.log("No users available") 
        }
      })
      .catch(error => {
        console.log("Error in loading users")
      });

}};

export const addReply = (reply, threadId) => {
  return dispatch => {
    database.collection("threads").doc(threadId)
      .update({
        replies: firebase.firestore.FieldValue.arrayUnion(reply)
      })
      .then(() => {
          dispatch(updateReply(threadId, reply));
      })
      .catch(error => {
        console.log("Could not add the reply.",error);
      })
  }
}

export const connectToChat = username => {
    console.log("here");
    return dispatch => joinChat(username);

}

// Replaces the direct call to sendMessage in Form
export const sendToChat = msg =>  {
    return dispatch => sendMessage(msg);
}

export const isConnected = () => ({type: CONNECTED})

export const newMessage = messages => ({
    type: NEW_MESSAGE,
    payload: {
        messages
    }
})
