import {useDispatch, useSelector} from "react-redux"; // Automatically passes the store to all child components
import store from "../redux/store"; // The store and main reducer
import { getPosts, storeUsers, getUsers } from "../redux/actions";
import { useEffect } from "react";
import { joinChat } from "../client";
import MainApp from "../components/MainApp";
import Header from "../components/Header";

import Messages from "../components/Messages";
import Form from "../components/Form";
import JoinChat from "../components/JoinChat";
import Onboarding from "../components/Onboarding";


const App = () =>  {

    const dispatch = useDispatch();
    const isConnected = useSelector(state => state.isConnected);
    const isNewUser = useSelector(state => state.isNewUser);
    // dispatch(getPosts());

    useEffect( () => {
      dispatch(getPosts());
      dispatch(getUsers());
    }, [])

    return (

      <> 
        <Header/>
        {
          (isNewUser === undefined || isNewUser === false) ?
          <MainApp/>
          :
          <Onboarding />
        }
      </>
    )
}
export default App;
